mapper.select:
select nideshop_goods_specification.id, nideshop_goods_specification.goods_id, nideshop_goods_specification.specification_id, nideshop_goods_specification.value, nideshop_goods_specification.pic_url, nideshop_goods.name goods_name, nideshop_specification.name specification_name from nideshop_goods_specification LEFT JOIN nideshop_goods ON nideshop_goods.id = nideshop_goods_specification.goods_id LEFT JOIN nideshop_specification ON nideshop_specification.id = nideshop_goods_specification.specification_id WHERE 1=1
AND nideshop_goods.name LIKE concat('%',#{goodsName},'%')
AND nideshop_goods_specification.goods_id = #{goodsId}
AND nideshop_goods_specification.specification_id = #{specificationId}
order by ${sidx} ${order}
order by nideshop_goods_specification.id desc
limit #{offset}, #{limit}

select count(*) from nideshop_goods_specification
LEFT JOIN nideshop_goods ON nideshop_goods.id = nideshop_goods_specification.goods_id
WHERE 1=1

    AND nideshop_goods.name LIKE concat('%',#{goodsName},'%')


    AND nideshop_goods_specification.goods_id = #{goodsId}


    AND nideshop_goods_specification.specification_id = #{specificationId}
